function Card({props, navigations}:{props:any, navigations:any}) {
  const {setState} = React.useContext(DataState);

  return (
    <View style={{width: '100%', elevation: 2, backgroundColor: '#FFFFFF'}}>
      <TouchableOpacity
        onPress={() => {
          navigations.navigate('ProductPage');
          setState((pv: object) => ({
            ...pv,
            name: props.name,
            details: props.detail,
            image: props.image,
            price: props.price,
          }));
        }}>
        <Image
          style={{height: 150}}
          source={{
            uri: props.image,
          }}
        />
        <View style={{paddingHorizontal: 6, paddingVertical: 6}}>
          <Text style={{...FontStyle.meduim, fontSize: 20, color: 'black'}}>
            {props.name}
          </Text>
          <Text style={{...FontStyle.regular, fontSize: 16, color: 'black'}}>
            {props.detail}
          </Text>
        </View>
      </TouchableOpacity>

      <View
        style={{
          borderTopWidth: 1,
          marginTop: 10,
          height: 50,
          backgroundColor: 'white',
          paddingHorizontal: 10,
          flexDirection: 'row',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}>
        <Text style={{...FontStyle.meduim, fontSize: 20, color: 'black'}}>
          $ {props.price}
        </Text>
        <TouchableOpacity
          style={{
            backgroundColor:'#1BCABF',
            width: 40,
            height: 40,
            justifyContent: 'center',
            alignItems: 'center',
            borderRadius: 4,
          }}
          onPress={() => {
            setState(pv => ({
              ...pv,
              cartList: [
                ...pv.cartList,
                {
                  name: props.name,
                  details: props.detail,
                  image: props.image,
                  price: props.price,
                },
              ],
            }));
          }}>
          <CartIcon color="white" />
        </TouchableOpacity>
      </View>
    </View>
  );
}

